import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Cadastro({ onSubmit }) {
  const [formData, setFormData] = useState({
    nome: '',
    cpf: '',
    dataNascimento: '',
    estadoCivil: 'solteiro',
    telefone: '',
    arquivoPDF: null,
    arquivoTXT: null,
  });

  const navigate = useNavigate();

  const handleChange = (e) => {
    if (e.target.type === 'file') {
      setFormData({ ...formData, [e.target.name]: e.target.files[0] });
    } else {
      const { name, value } = e.target;
      if (name === 'cpf') {
        const formattedCPF = value
          .replace(/\D/g, '') // Remove tudo que não for número
          .replace(/(\d{3})(\d)/, '$1.$2')
          .replace(/(\d{3})(\d)/, '$1.$2')
          .replace(/(\d{3})(\d{1,2})$/, '$1-$2');
        setFormData({ ...formData, [name]: formattedCPF });
      } else if (name === 'telefone') {
        const formattedPhone = value
          .replace(/\D/g, '') // Remove tudo que não for número
          .replace(/^(\d{2})(\d)/g, '($1) $2')
          .replace(/(\d{5})(\d)/, '$1-$2');
        setFormData({ ...formData, [name]: formattedPhone });
      } else {
        setFormData({ ...formData, [name]: value });
      }
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Verifica se ambos os arquivos foram anexados
    if (!formData.arquivoPDF || !formData.arquivoTXT) {
      alert('Por favor, anexe ambos os arquivos PDF e TXT.');
      return;
    }

    // Verifica se todos os campos obrigatórios foram preenchidos
    if (!formData.nome || !formData.cpf || !formData.dataNascimento || !formData.estadoCivil || !formData.telefone) {
      alert('Por favor, preencha todos os campos obrigatórios.');
      return;
    }

    // Chama a função onSubmit com os dados do formulário
    onSubmit(formData);
    navigate('/'); // Redireciona para a página inicial ou lista de usuários
  };

  const handleCancel = () => {
    navigate('/'); // Redireciona para a página inicial ou lista de usuários
  };

  return (
    <div className="page">
      <h2>Cadastrar Novo Usuário</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nome:</label>
          <input 
            type="text" 
            name="nome" 
            value={formData.nome} 
            onChange={handleChange} 
            required 
          />
        </div>
        <div className="form-group">
          <label>CPF:</label>
          <input
            type="text"
            name="cpf"
            value={formData.cpf}
            onChange={handleChange}
            placeholder="XXX.XXX.XXX-XX"
            maxLength="14"
            required
          />
        </div>
        <div className="form-group">
          <label>Data de Nascimento:</label>
          <input 
            type="date" 
            name="dataNascimento" 
            value={formData.dataNascimento} 
            onChange={handleChange} 
            required 
          />
        </div>
        <div className="form-group">
          <label>Estado Civil:</label>
          <select 
            name="estadoCivil" 
            value={formData.estadoCivil} 
            onChange={handleChange}
          >
            <option value="solteiro">Solteiro</option>
            <option value="casado">Casado</option>
            <option value="viuvo">Viúvo</option>
            <option value="separado">Separado</option>
          </select>
        </div>
        <div className="form-group">
          <label>Telefone:</label>
          <input
            type="text"
            name="telefone"
            value={formData.telefone}
            onChange={handleChange}
            placeholder="(XX) XXXXX-XXXX"
            maxLength="15"
            required
          />
        </div>
        <div className="form-group">
          <label>Anexar PDF:</label>
          <input 
            type="file" 
            name="arquivoPDF" 
            accept=".pdf" 
            onChange={handleChange} 
            required 
          />
        </div>
        <div className="form-group">
          <label>Anexar TXT:</label>
          <input 
            type="file" 
            name="arquivoTXT" 
            accept=".txt" 
            onChange={handleChange} 
            required 
          />
        </div>
        <div className="form-group">
          <button type="submit">Cadastrar</button>
          <button type="button" onClick={handleCancel} style={{ marginLeft: '10px' }}>
            Cancelar
          </button>
        </div>
      </form>
    </div>
  );
}

export default Cadastro;
