import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function UsuarioDetalhes({ usuarios, onEdit }) {
  const { id } = useParams();
  const usuario = usuarios.find((u) => u.id === parseInt(id));

  const [formData, setFormData] = useState(usuario);
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value, type } = e.target;

    if (type === 'file') {
      setFormData({ ...formData, [name]: e.target.files[0] });
    } else {
      // Impedir letras e permitir apenas números para o campo de data de nascimento
      if (name === 'dataNascimento') {
        // Permitir somente números e limitar a 4 caracteres
        const formattedValue = value.replace(/\D/g, '').slice(0, 4);
        setFormData({ ...formData, [name]: formattedValue });
      } else {
        setFormData({ ...formData, [name]: value });
      }
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onEdit(formData);
    navigate('/');
  };

  return (
    <div className="page">
      <h2>Editar Usuário</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nome:</label>
          <input type="text" name="nome" value={formData.nome} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>CPF:</label>
          <input type="text" name="cpf" value={formData.cpf} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Data de Nascimento (Ano):</label>
          <input
            type="text"
            name="dataNascimento"
            value={formData.dataNascimento}
            onChange={handleChange}
            required
            maxLength="4" // Garante que o input tenha no máximo 4 caracteres
            pattern="\d{4}" // Garante que o input seja composto de 4 dígitos
            placeholder="Ano (ex: 1990)"
          />
        </div>
        <div className="form-group">
          <label>Estado Civil:</label>
          <select name="estadoCivil" value={formData.estadoCivil} onChange={handleChange}>
            <option value="solteiro">Solteiro</option>
            <option value="casado">Casado</option>
            <option value="viuvo">Viúvo</option>
            <option value="separado">Separado</option>
          </select>
        </div>
        <div className="form-group">
          <label>Telefone:</label>
          <input type="text" name="telefone" value={formData.telefone} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Anexar PDF:</label>
          <input type="file" name="arquivoPDF" accept=".pdf" onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Anexar TXT:</label>
          <input type="file" name="arquivoTXT" accept=".txt" onChange={handleChange} required />
        </div>
        <button type="submit">Salvar Alterações</button>
      </form>
    </div>
  );
}

export default UsuarioDetalhes;
