import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

function UsuarioDetalhes({ usuarios, onEdit }) {
  const { id } = useParams();
  const usuario = usuarios.find((u) => u.id === parseInt(id));

  const [formData, setFormData] = useState(usuario);
  const [error, setError] = useState(null); // Estado para armazenar mensagens de erro
  const navigate = useNavigate();

  const handleChange = (e) => {
    if (e.target.type === 'file') {
      setFormData({ ...formData, [e.target.name]: e.target.files[0] });
    } else {
      setFormData({ ...formData, [e.target.name]: e.target.value });
    }
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Verifica se os arquivos PDF e TXT foram anexados
    if (!formData.arquivoPDF || !formData.arquivoTXT) {
      setError('Por favor, anexe ambos os arquivos PDF e TXT.');
      return;
    }

    // Se tudo estiver OK, chama a função de edição
    onEdit(formData);
    navigate('/');
  };

  return (
    <div className="page">
      <h2>Editar Usuário</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nome:</label>
          <input type="text" name="nome" value={formData.nome} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>CPF:</label>
          <input type="text" name="cpf" value={formData.cpf} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Data de Nascimento:</label>
          <input type="date" name="dataNascimento" value={formData.dataNascimento} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Estado Civil:</label>
          <select name="estadoCivil" value={formData.estadoCivil} onChange={handleChange}>
            <option value="solteiro">Solteiro</option>
            <option value="casado">Casado</option>
            <option value="viuvo">Viúvo</option>
            <option value="separado">Separado</option>
          </select>
        </div>
        <div className="form-group">
          <label>Telefone:</label>
          <input type="text" name="telefone" value={formData.telefone} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Anexar PDF:</label>
          <input type="file" name="arquivoPDF" accept=".pdf" onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label>Anexar TXT:</label>
          <input type="file" name="arquivoTXT" accept=".txt" onChange={handleChange} required />
        </div>

        {/* Exibe a mensagem de erro, se houver */}
        {error && <div className="error-message">{error}</div>}

        <button type="submit">Salvar Alterações</button>
      </form>
    </div>
  );
}

export default UsuarioDetalhes;
