import React from 'react';
import { Link } from 'react-router-dom';

function ListaUsuarios({ usuarios, onDelete, onExport, onSearch, pesquisa }) {
  return (
    <div className="page">
      <h2>Lista de Usuários</h2>
      <input 
        type="text" 
        placeholder="Pesquisar usuário..." 
        value={pesquisa} 
        onChange={onSearch} 
      />
      <ul>
        {usuarios.length > 0 ? usuarios.map((usuario) => (
          <li key={usuario.id}>
            <Link to={`/usuario/${usuario.id}`}>{usuario.nome}</Link>
            <button onClick={() => onDelete(usuario.id)}>Excluir</button>
          </li>
        )) : <p>Nenhum usuário encontrado.</p>}
      </ul>
      <div className="actions">
        <Link to="/cadastrar">
          <button>Cadastrar Novo Usuário</button>
        </Link>
        <button onClick={() => onExport('csv')}>Exportar CSV</button>
        <button onClick={() => onExport('txt')}>Exportar TXT</button>
      </div>
    </div>
  );
}

export default ListaUsuarios;
