import React from 'react';
import { Link } from 'react-router-dom';

// Definindo a função exportCSV dentro do mesmo arquivo
function exportCSV(users) {
  const csvRows = [];

  // Adiciona os cabeçalhos das colunas
  const headers = ['ID', 'Nome', 'CPF', 'Data de Nascimento', 'Estado Civil', 'Telefone'];
  csvRows.push(headers.join(','));

  // Adiciona as linhas com os dados dos usuários
  for (const user of users) {
    const values = [
      user.id,
      user.nome,
      user.cpf,
      user.dataNascimento,
      user.estadoCivil,
      user.telefone
    ];
    csvRows.push(values.join(','));
  }

  // Cria o blob e faz o download do arquivo CSV
  const csvContent = csvRows.join('\n');
  const blob = new Blob([csvContent], { type: 'text/csv;charset=utf-8;' });
  const url = URL.createObjectURL(blob);

  const link = document.createElement('a');
  link.setAttribute('href', url);
  link.setAttribute('download', 'usuarios.csv');
  document.body.appendChild(link);
  link.click();
  document.body.removeChild(link);
}

function ListaUsuarios({ usuarios, onDelete, onExport, onSearch, pesquisa }) {
  const handleExport = (type) => {
    if (type === 'csv') {
      exportCSV(usuarios);
    }
    // Adicione lógica para outros formatos de exportação (como TXT) aqui, se necessário.
  };

  return (
    <div className="page">
      <h2>Lista de Usuários</h2>
      <input
        type="text"
        placeholder="Pesquisar usuário por nome ou CPF..."
        value={pesquisa}
        onChange={onSearch}
      />
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nome</th>
            <th>CPF</th>
            <th>Data de Nascimento</th>
            <th>Estado Civil</th>
            <th>Telefone</th>
            <th>PDF</th>
            <th>TXT</th>
            <th>Ações</th>
          </tr>
        </thead>
        <tbody>
          {usuarios.length > 0 ? usuarios.map((usuario) => (
            <tr key={usuario.id}>
              <td>{usuario.id}</td>
              <td>{usuario.nome}</td>
              <td>{usuario.cpf}</td>
              <td>{usuario.dataNascimento}</td>
              <td>{usuario.estadoCivil}</td>
              <td>{usuario.telefone}</td>
              <td>{usuario.arquivoPDF ? <a href={URL.createObjectURL(usuario.arquivoPDF)} download={`Usuario_${usuario.nome}.pdf`}>Baixar</a> : 'N/A'}</td>
              <td>{usuario.arquivoTXT ? <a href={URL.createObjectURL(usuario.arquivoTXT)} download={`Usuario_${usuario.nome}.txt`}>Baixar</a> : 'N/A'}</td>
              <td>
                <Link to={`/usuario/${usuario.id}`}>Editar</Link>
                <button onClick={() => onDelete(usuario.id)}>Excluir</button>
              </td>
            </tr>
          )) : <tr><td colSpan="9">Nenhum usuário encontrado.</td></tr>}
        </tbody>
      </table>
      <div className="actions">
        <Link to="/cadastrar">
          <button>Cadastrar Novo Usuário</button>
        </Link>
        <button onClick={() => handleExport('csv')}>Exportar CSV</button>
        <button onClick={() => handleExport('txt')}>Exportar TXT</button>
      </div>
    </div>
  );
}

export default ListaUsuarios;
