import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

function CadastroUsuario() {
  const [nome, setNome] = useState('');
  const [cpf, setCpf] = useState('');
  const [dataNascimento, setDataNascimento] = useState('');
  const [estadoCivil, setEstadoCivil] = useState('');
  const [telefone, setTelefone] = useState('');
  const [arquivoPDF, setArquivoPDF] = useState(null);
  const [arquivoTXT, setArquivoTXT] = useState(null);
  const navigate = useNavigate();

  const handleSubmit = (e) => {
    e.preventDefault();

    if (!arquivoPDF || !arquivoTXT) {
      alert('Por favor, anexe ambos os arquivos PDF e TXT.');
      return;
    }

    // Lógica para enviar os dados do formulário
    const formData = new FormData();
    formData.append('nome', nome);
    formData.append('cpf', cpf);
    formData.append('dataNascimento', dataNascimento);
    formData.append('estadoCivil', estadoCivil);
    formData.append('telefone', telefone);
    formData.append('arquivoPDF', arquivoPDF);
    formData.append('arquivoTXT', arquivoTXT);

    // Aqui você pode adicionar a lógica para enviar o formData para o servidor

    // Após o envio, redirecionar ou exibir uma mensagem de sucesso
    navigate('/lista'); // Supondo que você deseja redirecionar para a lista de usuários
  };

  return (
    <div className="page">
      <h2>Cadastro de Usuário</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nome:</label>
          <input 
            type="text" 
            value={nome} 
            onChange={(e) => setNome(e.target.value)} 
            required 
          />
        </div>
        <div className="form-group">
          <label>CPF:</label>
          <input 
            type="text" 
            value={cpf} 
            onChange={(e) => setCpf(e.target.value)} 
            required 
          />
        </div>
        <div className="form-group">
          <label>Data de Nascimento:</label>
          <input 
            type="date" 
            value={dataNascimento} 
            onChange={(e) => setDataNascimento(e.target.value)} 
            required 
          />
        </div>
        <div className="form-group">
          <label>Estado Civil:</label>
          <input 
            type="text" 
            value={estadoCivil} 
            onChange={(e) => setEstadoCivil(e.target.value)} 
            required 
          />
        </div>
        <div className="form-group">
          <label>Telefone:</label>
          <input 
            type="text" 
            value={telefone} 
            onChange={(e) => setTelefone(e.target.value)} 
            required 
          />
        </div>
        <div className="form-group">
          <label>Arquivo PDF:</label>
          <input 
            type="file" 
            accept=".pdf" 
            onChange={(e) => setArquivoPDF(e.target.files[0])} 
            required 
          />
        </div>
        <div className="form-group">
          <label>Arquivo TXT:</label>
          <input 
            type="file" 
            accept=".txt" 
            onChange={(e) => setArquivoTXT(e.target.files[0])} 
            required 
          />
        </div>
        <button type="submit">Cadastrar</button>
      </form>
    </div>
  );
}

export default CadastroUsuario;
